(()=>{"use strict";document.getElementById("saveTabs").addEventListener("click",(function(){chrome.identity.getProfileUserInfo((function(e){const t=e.id;chrome.tabs.query({},(function(e){const s=e.map((e=>e.url));chrome.storage.sync.set({userId:t,savedTabs:s},(function(){alert("Tabs saved successfully! ✔")}))}))}))})),document.getElementById("retrieveTabs").addEventListener("click",(function(){chrome.identity.getProfileUserInfo((function(e){const t=e.id;chrome.storage.sync.get(["userId","savedTabs"],(function(e){if(e.userId===t){const t=e.savedTabs;t&&t.length>0?t.forEach((e=>{chrome.tabs.create({url:e})})):alert("No saved tabs found! ❌")}else alert("You are not authorized to retrieve these tabs! ❌")}))}))}))})();